export default {
  name: "TaskForm",
  props: {
    initialData: {
      type: Object,
      default: () => ({
        name: "",
        description: null,
        status: "low",
        completed: false,
        expires_at: null
      })
    }
  },
  data() {
    return {
      formData: {
        ...this.initialData
      }
    };
  },
  watch: {
    // Si cambia initialData (por ejemplo al editar), actualiza formData
    initialData: {
      deep: true,
      handler(newVal) {
        this.formData = {
          ...newVal
        };
      }
    }
  }
};
