import type { DirectiveOptions } from "vue";

// Definir el tipo de la directiva
const clickOutsideDirective: DirectiveOptions = {
  bind(el, binding, vnode) {
    const handler = (event: MouseEvent) => {
      if (!(el === event.target || el.contains(event.target as Node))) {
        const method = binding.expression;

        // Aseguramos que el método existe en el contexto de Vue
        if (
          method &&
          vnode.context &&
          typeof vnode.context[method as keyof typeof vnode.context] ===
            "function"
        ) {
          (vnode.context as any)[method](event); // Llamamos al método
        }
      }
    };

    (el as any).clickOutsideEvent = handler;
    document.body.addEventListener("click", handler);
  },

  unbind(el) {
    document.body.removeEventListener("click", (el as any).clickOutsideEvent);
  },
};

export default clickOutsideDirective;

// src/components/NotificationPlugin/index.ts
import Vue, { PluginObject } from "vue";
import Notifications from "./Notifications.vue";

interface NotificationOptions {
  message?: string;
  type?: string;
  timeout?: number;
  showClose?: boolean;
  closeOnClick?: boolean;
  verticalAlign?: string;
  horizontalAlign?: string;
  overlap?: boolean;
  timestamp?: Date;
  [key: string]: any;
}

interface NotificationStoreType {
  state: NotificationOptions[];
  settings: NotificationOptions;
  setOptions: (options: Partial<NotificationOptions>) => void;
  removeNotification: (timestamp: Date) => void;
  addNotification: (notification: NotificationOptions | string) => void;
  notify: (notification: NotificationOptions | NotificationOptions[]) => void;
}

const NotificationStore: NotificationStoreType = {
  state: [],
  settings: {
    overlap: false,
    verticalAlign: "top",
    horizontalAlign: "right",
    type: "info",
    timeout: 5000,
    closeOnClick: true,
    showClose: true,
  },
  setOptions(options) {
    this.settings = Object.assign(this.settings, options);
  },
  removeNotification(timestamp) {
    const indexToDelete = this.state.findIndex(
      (n) => n.timestamp?.getTime() === timestamp.getTime()
    );
    if (indexToDelete !== -1) {
      this.state.splice(indexToDelete, 1);
    }
  },
  addNotification(notification) {
    if (typeof notification === "string") {
      notification = { message: notification };
    }
    const ts = new Date();
    ts.setMilliseconds(ts.getMilliseconds() + this.state.length);
    notification.timestamp = ts;

    notification = Object.assign({}, this.settings, notification);
    this.state.push(notification);
  },
  notify(notification) {
    if (Array.isArray(notification)) {
      notification.forEach((n) => this.addNotification(n));
    } else {
      this.addNotification(notification);
    }
  },
};

const NotificationsPlugin: PluginObject<Partial<NotificationOptions>> = {
  install(VueInstance, options) {
    const app = new Vue({
      data: {
        notificationStore: NotificationStore,
      },
      methods: {
        notify(notification: NotificationOptions | NotificationOptions[]) {
          this.notificationStore.notify(notification);
        },
      },
    });

    VueInstance.prototype.$notify = (
      notification: NotificationOptions | NotificationOptions[]
    ) => app.notify(notification);

    VueInstance.prototype.$notifications = NotificationStore;
    VueInstance.component("Notifications", Notifications);

    if (options) {
      NotificationStore.setOptions(options);
    }
  },
};

export default NotificationsPlugin;

import Vue, { PluginFunction } from "vue";
import Sidebar from "./SideBar.vue";
import SidebarItem from "./SidebarItem.vue";

interface SidebarLink {
  name?: string;
  path?: string;
  icon?: string;
  [key: string]: any;
}

interface SidebarStoreType {
  showSidebar: boolean;
  sidebarLinks: SidebarLink[];
  isMinimized: boolean;
  displaySidebar: (value: boolean) => void;
  toggleMinimize: () => void;
}

const SidebarStore: SidebarStoreType = {
  showSidebar: false,
  sidebarLinks: [],
  isMinimized: false,
  displaySidebar(value: boolean) {
    this.showSidebar = value;
  },
  toggleMinimize() {
    document.body.classList.toggle("sidebar-mini");

    const simulateWindowResize = setInterval(() => {
      window.dispatchEvent(new Event("resize"));
    }, 180);

    setTimeout(() => {
      clearInterval(simulateWindowResize);
    }, 1000);

    this.isMinimized = !this.isMinimized;
  },
};

const SidebarPlugin: { install: PluginFunction<any> } = {
  install(Vue, options?: { sidebarLinks?: SidebarLink[] }) {
    if (options?.sidebarLinks) {
      SidebarStore.sidebarLinks = options.sidebarLinks;
    }

    const app = new Vue({
      data: {
        sidebarStore: SidebarStore,
      },
    });

    Vue.prototype.$sidebar = app.$data.sidebarStore;
    Vue.component("side-bar", Sidebar);
    Vue.component("sidebar-item", SidebarItem);
  },
};

export default SidebarPlugin;

